import React, { useState, useEffect } from 'react';
import { Card, Col, Container, Row, Table, Pagination, Form, Button } from 'react-bootstrap';
import NavigationBar from '../navbar/NavigationBar';
import { useNavigate } from 'react-router-dom';
import { findSales, getStockData } from '../../service/stock/StockService';

const MyStock = () => {
    const [stockData, setStock] = useState([]);
    const [query, setQuery] = useState('');
    const [isFiltersVisible, setFiltersVisible] = useState(false);
    const [pageSize, setPageSize] = useState(5);
    const navigate = useNavigate();

    const iconStyle = {
        color: '#ffc107',
        display: 'flex',
        justifyContent: 'flex-end',
    };

    const hideBlockButtonStyle = {
        color: '#333333',
        display: 'flex',
        justifyContent: 'flex-end',
    };

    useEffect(() => {
        // Fetch initial data when the component mounts
        const page = 0;
        handleStock();
    }, [pageSize]);

    const handleStock = () => {
        getStockData(query)
            .then((data) => {
                setStock(data.data);
            })
            .catch((error) => {
                console.error('Erro ao buscar dados do estoque', error);
                // Trate o erro adequadamente
            });
    };

    const handleSearchQuery = (value) => {
        setQuery(value)
        handleStock()
    }

    const handleFilterVisibility = () => {
        setFiltersVisible(!isFiltersVisible);
    }

    const handleRemoveItem = (index) => {
        // Lógica para remover o item do estoque
    }

    const renderPaginationItems = () => {
        // Implemente a renderização dos itens de paginação conforme necessário
    }

    return (
        <div>
            <NavigationBar />
            <Container className="mt-4">
                <Card>
                    <Card.Header>
                        <Row>
                            <Col>
                                <Card.Title style={{ color: '#333333' }}>
                                    Filtros
                                </Card.Title>
                            </Col>
                            <Col>
                                <div style={hideBlockButtonStyle}>
                                    <Button size="sm" onClick={handleFilterVisibility} variant="outline-warning">
                                        {isFiltersVisible ? <i className="fa-solid fa-angle-up"></i> : <i className="fa-solid fa-angle-down"></i>}
                                    </Button>
                                </div>
                            </Col>
                        </Row>
                    </Card.Header>
                    {isFiltersVisible && (
                        <Card.Body>
                            <Row className="mt-4">
                                <Col sm={1}>
                                    Buscar
                                </Col>
                                <Col sm={9}>
                                    <Form.Group className="mb-3" controlId="formGridProduct">
                                        <Form.Control
                                            type="text"
                                            id="formGridProduct"
                                            value={query}
                                            onChange={(e) => handleSearchQuery(e.target.value)}
                                        />
                                    </Form.Group>
                                </Col>
                                <Col sm={2}>
                                    <Button
                                        style={{ width: '100%' }}
                                        onClick={() => setQuery('')} // Limpa o campo de consulta
                                        variant="outline-secondary"
                                    >
                                        Limpar
                                    </Button>
                                </Col>
                            </Row>
                        </Card.Body>
                    )}
                </Card>
                <Card className="text-left">
                    <Card.Body>
                        <Row>
                            <Col>
                                <Card.Title style={{ color: '#333333' }}>Estoque</Card.Title>
                            </Col>
                            <Col>
                                <div style={iconStyle}>
                                    <i className="fa-solid fa-table"></i>
                                </div>
                            </Col>
                        </Row>
                        <Table striped hover responsive>
                            <thead>
                                <tr>
                                    <th>Produto</th>
                                    <th>x</th>
                                </tr>
                            </thead>
                            <tbody>
                                {stockData.map((item, index) => (
                                    <tr key={index}>
                                        <td>{item.name}</td>
                                        <td className='col-1'>
                                            <Button
                                                variant="danger"
                                                onClick={() => handleRemoveItem(index)}
                                            >
                                                <i className="fa-solid fa-times"></i>
                                            </Button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </Table>
                    </Card.Body>
                    <Card.Footer>
                        <div className="d-flex justify-content-between">
                            <Form.Group>
                                <Form.Control
                                    as="select"
                                    value={pageSize}
                                    onChange={null}
                                >
                                    <option value="5">5</option>
                                    <option value="10">10</option>
                                    <option value="20">20</option>
                                    <option value="50">50</option>
                                </Form.Control>
                            </Form.Group>
                            <Pagination>{renderPaginationItems()}</Pagination>
                        </div>
                    </Card.Footer>
                </Card>
            </Container>
        </div>
    );
};

export default MyStock;
