import React, {useState, useEffect} from 'react';
import {Card, Col, Container, Row, Table, Pagination, Form, Button} from 'react-bootstrap';
import NavigationBar from '../navbar/NavigationBar';
import {useNavigate} from 'react-router-dom';
import {logout} from '../../service/auth/AuthenticationService';
import {findSales, getSalesData} from '../../service/saleshistory/SalesHistoryService';

const MyStock = () => {
    const [salesHistory, setStock] = useState([]);
    const [salesData, setSalesData] = useState(null);
    const [totalPages, setTotalPages] = useState(0);
    const [query, setQuery] = useState('');
    const [isFiltersVisible, setFiltersVisible] = useState(false);
    const [searchTimer, setSearchTimer] = useState(null);
    const [currentPage, setCurrentPage] = useState(0);
    const [pageSize, setPageSize] = useState(5);
    const navigate = useNavigate();

    const iconStyle = {
        color: '#ffc107',
        display: 'flex',
        justifyContent: 'flex-end',
    };


    const hideBlockButtonStyle = {
        color: '#333333',
        display: 'flex',
        justifyContent: 'flex-end',
    };

    useEffect(() => {
        // Fetch initial data when the component mounts
        const page = 0;
        handleStockSearch(page, pageSize);
        handleStock();
    }, [pageSize]);



    const handleStock = () => {
        let tmpObj =  [
            {
                produto: "macarrão",
                dataVencimento: "31/10/2024" ,
                dataCompra: "31/05/2024" 
            }
        ]
        setSalesData(tmpObj);
        getSalesData()
            .then((data) => {
                let tmpObj =  [
                    {
                        produto: "macarrão",
                        dataVencimento: "31/10/2024" ,
                        dataCompra: "31/05/2024" 
                    }
                ]
                console.log(tmpObj)
                setSalesData(tmpObj);
            })
            .catch((error) => {
                // console.error('Sales data fetch failed', error);
                // if (error.message.includes('token.invalid_or_expired')) {
                //     logout();
                //     navigate('/');
                // }
            });
    };

    const handlePageChange = (page) => {
        // Update the current page and fetch data for the new page
        setCurrentPage(page);
        handleStockSearch(page, pageSize);
    };

    const handleMonthOnChange = (e) => {
        const selectedMonth = e.target.value;
        setMonth(parseInt(selectedMonth, 10));
    };

    const handleYearOnChange = (e) => {
        const selectedYear = e.target.value;
        setYear(parseInt(selectedYear, 10));
    }

    const handlePageSizeChange = (event) => {
        // Update the page size when the input field changes
        const newSize = parseInt(event.target.value, 10);
        setPageSize(newSize);
        setCurrentPage(0); // Reset to the first page
        handleStockSearch(0, newSize);
    };

    const renderPaginationItems = () => {
        const items = [];
        for (let number = 0; number < totalPages; number++) {
            items.push(
                <Pagination.Item
                    key={number}
                    active={number === currentPage}
                    onClick={() => handlePageChange(number)}
                >
                    {number + 1}
                </Pagination.Item>
            );
        }
        return items;
    };

    const handleMonthVisibility = () => {
        setMonthStatsVisible(!isMonthStatsVisible)
    }

    const handleFilterVisibility = () => {
        setFiltersVisible(!isFiltersVisible)
    }

    const formatCurrency = (value) => {
        return value ? `R$ ${value.toLocaleString('pt-BR', {minimumFractionDigits: 2})}` : "R$0,00";
    };

    const handleSearchQuery = (value) => {
        setQuery(value);

        if (searchTimer) {
            clearTimeout(searchTimer);
        }

        const newTimer = setTimeout(() => {
            handleStockSearch(0, pageSize);
        }, 600);

        setSearchTimer(newTimer);
    };
    const handleCleanSearch = () => {
        setQuery('');

        setTimeout(()=>{
            handleStockSearch(0, pageSize);
        }, 600)
    };


    return (
        <div>
            <NavigationBar/>
            <Container className="mt-4">
                <Card>
                    <Card.Header>
                        <Row>
                            <Col>
                                <Card.Title style={{color: '#333333'}}>
                                    Filtros
                                </Card.Title>
                            </Col>
                            <Col>
                                <div style={hideBlockButtonStyle}>
                                    <Button size="sm"  onClick={handleFilterVisibility} variant="outline-warning">
                                        {
                                            isFiltersVisible && (
                                                <i className="fa-solid fa-angle-up"></i>
                                            )
                                        }
                                        {
                                            !isFiltersVisible && (
                                                <i className="fa-solid fa-angle-down"></i>
                                            )
                                        }
                                    </Button>
                                </div>
                            </Col>
                        </Row>
                    </Card.Header>
                    {
                        isFiltersVisible && (
                            <Card.Body>
                                <Row className="mt-4">
                                    <Col sm={1}>
                                        Buscar
                                    </Col>
                                    <Col sm={9}>
                                        <Form.Group className="mb-3" controlId="formGridProduct">
                                            <Form.Control
                                                type="text"
                                                id="formGridProduct"
                                                value={query}
                                                onChange={(e => handleSearchQuery(e.target.value))}
                                            />
                                        </Form.Group>
                                    </Col>
                                    <Col sm={2}>
                                        <Button
                                            style={{width: '100%'}}
                                            onClick={handleCleanSearch}
                                            variant="outline-secondary"
                                        >
                                            Limpar
                                        </Button>
                                    </Col>
                                </Row>
                            </Card.Body>
                        )
                    }
                </Card>
                <Card className="text-left">
                    <Card.Body>
                        <Row>
                            <Col>
                                <Card.Title style={{color: '#333333'}}>Estoque</Card.Title>
                            </Col>
                            <Col>
                                <div style={iconStyle}>
                                    <i className="fa-solid fa-table"></i>
                                </div>
                            </Col>
                        </Row>
                        <Table striped hover responsive>
                            <thead>
                            <tr>
                                <th>Produto</th>
                                <th>Data Compra</th>
                                <th>Vencimento</th>
                                <th>X</th>
                            </tr>
                            </thead>
                            <tbody>
                            {salesHistory.map((item, index) => (
                                <tr key={index}>
                                    <td className="col-3">{item.produto}</td>
                                    <td className="col-1">{item.dataVencimento}</td>
                                    <td className="col-2">{item.dataCompra}</td>
                                </tr>
                            ))}
                            </tbody>
                        </Table>
                    </Card.Body>
                    <Card.Footer>
                        <div className="d-flex justify-content-between">
                            <Form.Group>
                                <Form.Control
                                    as="select"
                                    value={pageSize}
                                    onChange={handlePageSizeChange}
                                >
                                    <option value="5">5</option>
                                    <option value="10">10</option>
                                    <option value="20">20</option>
                                    <option value="50">50</option>
                                </Form.Control>
                            </Form.Group>
                            <Pagination>{renderPaginationItems()}</Pagination>
                        </div>
                    </Card.Footer>
                </Card>
            </Container>
        </div>
    );
};

export default MyStock;
