import React, { useState, useEffect } from 'react';
import { Card, Col, Container, Row, Table, Pagination, Form, Button } from 'react-bootstrap';
import NavigationBar from '../navbar/NavigationBar';
import { useNavigate } from 'react-router-dom';
import { getStockData } from '../../service/stock/StockService';

const MyStock = () => {
    const [stockData, setStock] = useState([]);
    const [query, setQuery] = useState('');
    const [isFiltersVisible, setFiltersVisible] = useState(false);
    const [pageSize, setPageSize] = useState(5);
    const navigate = useNavigate();

    const iconStyle = {
        color: '#ffc107',
        display: 'flex',
        justifyContent: 'flex-end',
    };

    const hideBlockButtonStyle = {
        color: '#333333',
        display: 'flex',
        justifyContent: 'flex-end',
    };

    useEffect(() => {
        // Fetch initial data when the component mounts
        handleStock();
    }, []);

    const handleStock = () => {
        getStockData()
            .then((data) => {

        console.log(data)
                setStock(data); // Preenche a tabela com os dados retornados
            })
            .catch((error) => {
                console.error('Stock data fetch failed', error);
                // Handle error
            });
    };

    const handleFilterVisibility = () => {
        setFiltersVisible(!isFiltersVisible);
    };

    const renderPaginationItems = () => {
        // Renderiza os itens de paginação
    };

    return (
        <div>
            <NavigationBar />
            <Container className="mt-4">
                {/* Código do filtro omitido por brevidade */}

                <Card className="text-left">
                    <Card.Body>
                        <Row>
                            <Col>
                                <Card.Title style={{ color: '#333333' }}>Estoque</Card.Title>
                            </Col>
                            <Col>
                                <div style={iconStyle}>
                                    <i className="fa-solid fa-table"></i>
                                </div>
                            </Col>
                        </Row>
                        <Table striped hover responsive>
                            <thead>
                                <tr>
                                    <th>Produto</th>
                                    {/* Adicione mais colunas conforme necessário */}
                                </tr>
                            </thead>
                            <tbody>
                                {stockData.map((item, index) => (
                                    <tr key={index}>
                                        <td className="col-3">{item.name}</td>
                                        {/* Adicione mais colunas conforme necessário */}
                                    </tr>
                                ))}
                            </tbody>
                        </Table>
                    </Card.Body>
                    <Card.Footer>
                        <div className="d-flex justify-content-between">
                            <Form.Group>
                                <Form.Control
                                    as="select"
                                    value={pageSize}
                                    onChange={(e) => setPageSize(e.target.value)}
                                >
                                    <option value="5">5</option>
                                    <option value="10">10</option>
                                    <option value="20">20</option>
                                    <option value="50">50</option>
                                </Form.Control>
                            </Form.Group>
                            <Pagination>{renderPaginationItems()}</Pagination>
                        </div>
                    </Card.Footer>
                </Card>
            </Container>
        </div>
    );
};

export default MyStock;
